
public class Counter3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//System.out.print(14 % 7);;
		int counter = 1;
		while(counter <= 30) {
			if(counter % 7 == 0) {
				System.out.print("HAHAHA ");
			}else {
				System.out.print(counter + " ");
			}
			counter ++;
		}
	}

}
